import Styles from './acoesCard.module.css'
import { AiFillCloseCircle } from "react-icons/ai";
import Input from '../forms/Input';
import SelectAcoes from '../forms/SelectAcoes';
import { useState } from 'react';

function AcoesCards({id,nomeAcao,valor,dataDaAcao,tipo}){

    const [acoes,setAcoes]=useState([])
    const [filtro,setFiltro]=useState({})

    // fetch('http://localhost:5000/Acoes',{
    //     method:'GET',
    //     headers:{
    //         'Content-Type':'application/json'
    //     }
    // }).then(resp=>resp.json()).then(data=>setAcoes(data))

    function handleTipoAcao(e){
       
        setFiltro(filtro.tipoConta={
            id:e.target.value,
            tipo:e.target.options[e.target.selectedIndex].text   
        })

    }

    function handleFiltro(e){
        setFiltro({[e.target.name]:e.target.value})
       
    }
    
   

    return(
        <>
        <div className={Styles.divDosCards}>
  
            <div className={Styles.cardAcao}>
                <button className={Styles.fechar}><AiFillCloseCircle/></button>
                <h4 className={Styles.nome}>{nomeAcao}</h4>
                <p><span>Valor:</span>{valor}</p>
                <p><span>Data:</span>{dataDaAcao}</p>
                <p><span>Tipo:</span>{tipo}</p>
            </div>

        </div>
        </>
    )
}

export default AcoesCards
