function Select({name,array,text}){

    return(
    <div>
    <label htmlFor={name}>{text}</label>
    <select name={name}>
        
        { array.map((elemento)=>{
            return(
                <option key={elemento.id} value={elemento.tipo}>{elemento.tipo}</option>
            )
        })}
    </select>

    </div>

    )
}

export default Select
