import { useState } from "react"
import { Link } from "react-router-dom"
import Input from '../forms/Input'

function AcoesForms({text,ex,Acao,Conta,setConta}){

    const [acao,setAcao]=useState()

    // function handleAcao(e){
    //     setAcao({...acao,[e.target.name]:e.target.value})
    //     console.log(acao)
    // }
    
    return(
        <>
                <Input type='text' name='nomeacao'  text={`Nome do ${text}`} placeholder={`ex:${ex}`}  value={acao.nomeAcao ? acao.nomeAcao:''} ></Input>
                <Input type='number' name='valor' text={`Valor que deseja ${Acao}`} placeholder='ex:1000'  value={acao.valor ? acao.valor:''}></Input>
                <Input type='date' name='dataDaAcao' text={`Data do ${text}`}  value={acao.dataDaAcao ? acao.dataDaAcao:''}></Input>
                <Link to='/Contas'><button >Criar Conta</button></Link> 
        </>
    )
}

export default AcoesForms
