import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import ContaForms from "../Conta/ContaForms"
import Input from "../forms/Input"
import Select from "../forms/Select"
import Styles from './ContaEditar.module.css'


function EditarConta(){

    const idConta=useParams()
    const [Conta,setConta]=useState()
    const [Contas,setContas]=useState()
 
    const [tiposContas,setTiposContas]=useState([])

//...................................................................
    useEffect(()=>{
        fetch(`http://localhost:5000/contas/${idConta.id}`,{
            method:'GET',
            headers:{
                'Content-Type':'application/json'
            }
        }).then(resp=>resp.json()).then(data=>setConta(data))
    },[idConta])

//...................................................................

    useEffect(()=>{
        fetch(`http://localhost:5000/contas`,{
            method:'GET',
            headers:{
                'Content-Type':'application/json'
            }
        }).then(resp=>resp.json()).then(data=>setContas(data))
    },[])

//...................................................................

    useEffect(()=>{
        fetch('http://localhost:5000/tipoDeConta',{
            method:'GET',
            headers:{
                'Content-Type':'application/json'
            }
        }).then(resp=>resp.json()).then(data=>setTiposContas(data))
    },[])

//...................................................................

    function editarConta(){
       
    
    fetch(`http://localhost:5000/contas/${idConta}`,{
                method:'PATCH',
                headers:{
                    'Content-Type':'application/json'
                },
                body:JSON.stringify(Conta)
                }).then(resp=>resp.json()).then(data=>{
                    setConta(data)               
                }) 

  
    }
            
    function handleTipoConta(e){
       
        setConta({...Conta,tipoConta:{
            id:e.target.value,
            tipo:e.target.options[e.target.selectedIndex].text   
        }})
        console.log(Conta)

    }

    function handleConta(e){
        setConta({...Conta,[e.target.name]:e.target.value})
      
    }



    return(
        <>

            {Conta && (
                <>
                    <div className={Styles.Conta}>
                        <h2>{Contas.find(conta=>conta.id==idConta.id).nomeConta}</h2>
                        <p><span>Proprietário:</span> {Contas.find(conta=>conta.id==idConta.id).nomeProprietario}</p>
                        <p><span>Saldo:</span> R${Contas.find(conta=>conta.id==idConta.id).saldo}</p>
                        <p><span>CPF:</span> {Contas.find(conta=>conta.id==idConta.id).cpfProprietario}</p>
                        <p><span>Data de nascimento:</span> {Contas.find(conta=>conta.id==idConta.id).dataNascimento}</p>
                        <p><span>Tipo Conta:</span> {Conta.tipoConta.tipo}</p>  
                    </div>
        
                     <div className={Styles.forms}>
           
                        <Input type='text' name='nomeConta'  text='Nome da conta' placeholder='ex:Reactx' onChange={handleConta}  value={Conta.nomeConta ? Conta.nomeConta:''} ></Input>
                        <Input type='text' name='nomeProprietario'  text='Nome' placeholder='ex:João Ernesto' onChange={handleConta} value={Conta.nomeProprietario ? Conta.nomeProprietario:''} ></Input>
                        <Input type='text' name='cpfProprietario' text='CPF' placeholder='ex:704.261.843-27' onChange={handleConta}  value={Conta.cpfProprietario ? Conta.cpfProprietario:''}></Input>
                        <Input type='date' name='dataNascimento' text='Data de nascimento' onChange={handleConta}  value={Conta.dataNascimento ? Conta.dataNascimento:''}></Input>
                        <Select name='tipoConta' text='Tipo da Conta' array={tiposContas} onChange={handleTipoConta} value={Conta.tipoConta ? Conta.tipoConta.id:''} ></Select>
                        <button className={Styles.button} onClick={editarConta}>Criar Conta</button>
                    </div>
    
                    
                </>
                
            )}

        </>
    )
}

export default EditarConta
