//import { Link } from "react-router-dom"
import Input from '../forms/Input'
import Styles from './acoesForms.module.css'

function AcoesForms({text,ex,Acao,acao,setAcao,funcao}){


    function handleAcao(e){
      
        setAcao({...acao,[e.target.name]:e.target.value})
       
    }
    
    return(
        <div className={Styles.forms}>
                <Input type='text' name='nomeAcao'  text={`Nome do ${text}`} placeholder={`ex:${ex}`} onChange={handleAcao} value={acao.nomeAcao ? acao.nomeAcao:''} ></Input>
                <Input type='number' name='valor' text={`Valor que deseja ${Acao}`} placeholder='ex:1000' onChange={handleAcao} value={acao.valor ? acao.valor:''}></Input>
                <Input type='date' name='dataDaAcao' text={`Data do ${text}`} onChange={handleAcao} value={acao.dataDaAcao ? acao.dataDaAcao:''}></Input>
                <button onClick={funcao} className={Styles.button} >{acao}</button>
        </div>
    )
}

export default AcoesForms
