import ContaForms from "../Conta/ContaForms";
import Styles from './NewConta.module.css'

function NewConta(){

    const [conta,setConta]=useState({})
    const history=useNavigate()
    const Validator = require('cpf-rg-validator')
    const timeElapsed = Date.now();
    const today = new Date(timeElapsed)

    function adicionarConta(){

        conta.cpfProprietario=conta.cpfProprietario.substring(0,3) + '.' + conta.cpfProprietario.substring(3,6)+'.'+conta.cpfProprietario.substring(6,9) + '-' + conta.cpfProprietario.substring(9,11)
    
        if(!conta.dataNascimento || parseInt(today.toISOString().substring(0,4))-parseInt(conta.dataNascimento)>=140){
    
            fetch('',{}).then(()=>history('/Contas',{state:{messageDataInvalido:'Data inválida!'}})).catch(error=>console.log(error))
        
        }else if (!conta.nomeConta || conta.nomeConta.match(validarString)){
    
            fetch('',{}).then(()=>history('/Contas',{state:{messageDeNomeContaInvalida:'Nome da Conta Inválida!'}})).catch(error=>console.log(error))
     
        }else if (!conta.nomeProprietario || conta.nomeProprietario.match(validarString)){
    
            fetch('',{}).then(()=>history('/Contas',{state:{messageDeNomeProprietarioInvalida:'Nome do proprietário Inválido!'}})).catch(error=>console.log(error))
     
        }
        
        else if( !conta.tipoConta){
    
            fetch('',{}).then(()=>history('/Contas',{state:{messageDeTipoContaInvalido:'Tipo de conta não foi selecionada!'}})).catch(error=>console.log(error))
    
        }else if(!Validator.cpf(conta.cpfProprietario)){
    
            fetch('',{}).then(()=>history('/Contas',{state:{messageCpfInvalido:'Cpf inválido!'}})).catch(error=>console.log(error))
    
        }else if((parseInt(today.toISOString().substring(0,4))-parseInt(conta.dataNascimento.substring(0,4))<18) 
                 || (parseInt(today.toISOString().substring(0,4))-parseInt(conta.dataNascimento.substring(0,4))===18 && parseInt(today.toISOString().substring(5,7))<parseInt(conta.dataNascimento.substring(5,7)))
                 || (parseInt(today.toISOString().substring(0,4))-parseInt(conta.dataNascimento.substring(0,4))===18 && parseInt(today.toISOString().substring(5,7))===parseInt(conta.dataNascimento.substring(5,7)) && parseInt(today.toISOString().substring(8,10))<parseInt(conta.dataNascimento.substring(8,10)) )){
                
                fetch('',{}).then(()=>history('/Contas',{state:{messageDeMenorIdade:'Precisa ser maior de 18 anos!!'}})).catch(error=>console.log(error))
        }else{
    
                conta.saldo=0
                conta.acoes=[]
        
                fetch('http://localhost:5000/contas',{
                    method:'POST',
                    headers:{
                        'Content-Type':'application/json'
                    },
                    body:JSON.stringify(conta)
                }).then(resp=>resp.json()).then(()=>history('/Contas',{state:{messageCriacao:'Conta criado com sucesso!'}})).catch(error=>console.log(error))
                
            }
        }
        

    return(
        <>
            <div className={Styles.newConta}>
            <h2>Crie sua conta</h2>
            <h3>Comece agora a se organizar!!!</h3>
            <ContaForms adicionarConta={adicionarConta} conta={conta} setConta={setConta}/>

            </div>
        </>
    )
}
export default NewConta
