import { useState } from "react"

function AcoesForms({text,ex,Acao,Conta,setConta}){

    const [acao,setAcao]=useState()

    function handleAcao(e){
        setAcao({...acao,[e.target.name]:e.target.value})
        console.log(acao)
    }
    
    return(
        <>
                <Input type='text' name='nomeAcao'  text={`Nome do ${text}`} placeholder={`ex:${ex}`} onChange={handleAcao} value={acao.nomeAcao ? acao.nomeAcao:''} ></Input>
                <Input type='number' name='valor' text={`Valor que deseja ${Acao}`} placeholder='ex:1000' onChange={handleAcao} value={acao.cpfProprietario ? acao.cpfProprietario:''}></Input>
                <Input type='date' name='dataDaAcao' text={`Data do ${text}`} onChange={handleAcao} value={acao.dataNascimento ? acao.dataNascimento:''}></Input>
                <Link to='/Contas'><button className={Styles.button} onClick={adicionarAcao}>Criar Conta</button></Link> 
        </>
    )
}

export default AcoesForms
