import { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import ContaForms from "../Conta/ContaForms"
import Input from "../forms/Input"
import Select from "../forms/Select"
import Styles from './ContaEditar.module.css'

function EditarConta(){

    const idConta=useParams()
    const [Conta,setConta]=useState()
    const [tiposContas,setTiposContas]=useState([])

    useEffect(()=>{
        fetch(`http://localhost:5000/contas/${idConta.id}`,{
            method:'GET',
            headers:{
                'Content-Type':'application/json'
            }
        }).then(resp=>resp.json()).then(data=>setConta(data))
    },[idConta])

    useEffect(()=>{
        fetch('http://localhost:5000/tipoDeConta',{
            method:'GET',
            headers:{
                'Content-Type':'application/json'
            }
        }).then(resp=>resp.json()).then(data=>setTiposContas(data))
    },[])

    function handleTipoConta(e){
       
        setConta({...Conta,tipoConta:{
            id:e.target.value,
            tipo:e.target.options[e.target.selectedIndex].text   
        }})

    }

    function handleConta(e){
        setConta({...Conta,[e.target.name]:e.target.value})
       
    }



    return(
        <>

            {Conta && (
                <>
                    <div className={Styles.Conta}>
                        <h2>{Conta.nomeConta}</h2>
                        <p><span>Proprietário:</span> {Conta.nomeProprietario}</p>
                        <p><span>Saldo:</span> R${Conta.saldo}</p>
                        <p><span>CPF:</span> {Conta.cpfProprietario}</p>
                        <p><span>Data de nascimento:</span> {Conta.dataNascimento}</p>
                        <p><span>Tipo Conta:</span> {Conta.tipoConta.tipo}</p>  
                    </div>
        
                     <div>
           
                        <Input type='text' name='nomeConta'  text='Nome da conta' placeholder='ex:Reactx'  value={Conta.nomeConta ? Conta.nomeConta:''} ></Input>
                        <Input type='text' name='nomeProprietario'  text='Nome' placeholder='ex:João Ernesto'  value={Conta.nomeProprietario ? Conta.nomeProprietario:''} ></Input>
                        <Input type='text' name='cpfProprietario' text='CPF' placeholder='ex:704.261.843-27'  value={Conta.cpfProprietario ? Conta.cpfProprietario:''}></Input>
                        <Input type='date' name='dataNascimento' text='Data de nascimento'  value={Conta.dataNascimento ? Conta.dataNascimento:''}></Input>
                        <Select name='tipoConta' text='Tipo da Conta' array={tiposContas}  value={Conta.tipoConta ? Conta.tipoConta.id:''} ></Select>
                
                    </div>
    
                    
                </>
                
            )}

        </>
    )
}

export default EditarConta
